        return (
            <> 
            <div>
            <p> { name }</p>
            <p> { type }</p>
            <p> { `Average weight: ${averageWeight.value} ${averageWeight.measurementUnit}`}</p>
            </div>
            <img src/>
            </>
        )
    }
}

export default Pokemon;
